import React from "react";
import Slider from "infinite-react-carousel";
import { useState } from 'react';
import telefunken from "./data/telefunken.jpg";
import kent from "./data/kent.jpg";
import filtre from "./data/GREEN-FILTRE-PACK-3.jpg";
import rarrow from "./data/right-arrow.png";
import larrow from "./data/right-arrow.png";
import "./slide.css";

function Slide() {
    const [arrow_visibility, setArrow_visibility]=useState("hidden");

    const SlickArrowLeft = ({ currentSlide, slideCount, ...props }) => (
        <div onMouseMove={() => setArrow_visibility("visible")} onMouseLeave={() => setArrow_visibility("hidden")}>
        <img style={{visibility: arrow_visibility}} src={rarrow} alt="prevArrow" {...props} />
        </div>
        );
    const SlickArrowRight = ({ currentSlide, slideCount, ...props }) => (
        <img src={larrow} alt="nextArrow" {...props} />
      );
  const settings = {
    autoplay: true,
    autoplaySpeed: 5000,
    className: "slide_box",
    dots: true,
    virtualList: true,
    duration: 20,
    arrows:true,
    prevArrow: <SlickArrowLeft/>
  };
  return (
    <div>

      <Slider {...settings}>
        <div className="row" onMouseOver={() => setArrow_visibility("visible")} >

          <img
            src={telefunken}
            className="slide_img col-lg-12"
            alt="Responsive img"
          />

        </div>
        <div onMouseMove={() => setArrow_visibility("visible")} onMouseLeave={() => setArrow_visibility("hidden")}>
          <img
            src={kent}
            className="slide_img col-lg-12"
            alt="Responsive img"
          />

        </div>
        <div onMouseOver={() => setArrow_visibility("visible")} onMouseLeave={() => setArrow_visibility("hidden")}>
          <img
            src={filtre}
            className="slide_img col-lg-12"
            alt="Responsive img"
          />

        </div>
      </Slider>
    </div>
  );
}

export default Slide;
