import React from "react";
import Slider from "infinite-react-carousel";
import { useState } from 'react';
import telefunken from "./data/telefunken.jpg";
import kent from "./data/kent.jpg";
import filtre from "./data/GREEN-FILTRE-PACK-3.jpg";
import rarrow from "./data/right-arrow.png";
import larrow from "./data/right-arrow.png";
import "./slide.css";

function Slide() {
    const [arrow_visibility, setArrow_visibility]=useState(false);

    const SlickArrowLeft = ({ currentSlide, slideCount, ...props }) => (
        <img stysrc={rarrow} alt="prevArrow" {...props} />
      );
    const SlickArrowRight = ({ currentSlide, slideCount, ...props }) => (
        <img src={larrow} alt="nextArrow" {...props} />
      );
  const settings = {
    autoplay: true,
    autoplaySpeed: 5000,
    className: "slide_box",
    dots: true,
    virtualList: true,
    duration: 20,
    arrows:arrow_visibility,

  };
  return (
    <div>

      <Slider {...settings}>
        <div className="row" onMouseOver={() => setArrow_visibility(true)} onMouseLeave={() => setArrow_visibility(false)}>

          <img
            src={telefunken}
            className="slide_img col-lg-12"
            alt="Responsive img"
          />

        </div>
        <div>
          <img
            src={kent}
            className="slide_img col-lg-12"
            alt="Responsive img"
          />

        </div>
        <div>
          <img
            src={filtre}
            className="slide_img col-lg-12"
            alt="Responsive img"
          />

        </div>
      </Slider>
    </div>
  );
}

export default Slide;
